    private void btnAdicionarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnAdicionarActionPerformed
        Produto p = new Produto(txtNome.getText(), Double.parseDouble(txtPreco.getText()), Integer.parseInt(txtQtd.getText()));
        GerenciadorProdutos GP = new GerenciadorProdutos(txtNome.getText(), Double.parseDouble(txtPreco.getText()), Integer.parseInt(txtQtd.getText()));
        GP.adicionarProduto(p);
        System.out.println(GP.listaProdutos);
        
        txtArea.setText(GP.listarProdutos());
    }//GEN-LAST:event_btnAdicionarActionPerformed

